INSERT INTO `country_contents` (`id`, `country_id`, `description`, `youtube_iframe`, `button_name`, `button_link`, `button_target`, `layout`, `created_at`, `updated_at`, `title`, `meta_description`, `meta_tags`, `meta_title`) VALUES
(1, 3, '<p style=\"text-align:justify\"><span style=\"font-family:Poppins,sans-serif\"><span style=\"font-size:14px\">Pix, a fast payments system in Brazil, was developed between 2018 and 2020 by the Central Bank of Brazil (BCB).</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Poppins,sans-serif\"><span style=\"font-size:14px\">Launched in November 2020, Pix allows people to send and receive payments within a few seconds &mdash; typically around five seconds &mdash; at any time of the day, including non-business days.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Poppins,sans-serif\"><span style=\"font-size:14px\">About 77% of Brazil&rsquo;s population has used the system.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Poppins,sans-serif\"><span style=\"font-size:14px\">Pix has also contributed significantly to financial inclusion, with 71 million Brazilians making their first digital payments using Pix.</span></span></p>', 'https://youtu.be/SwXGl7ykJvE', 'Read More', 'https://www.codevelop.fund/zambia-digital-health', '_blank', 'left', '2025-07-26 04:53:48', '2025-08-06 10:21:02', 'Pix • Instant Payments', 'Pix • Instant Payments', 'Pix • Instant Payments', 'Pix • Instant Payments'),
(2, 4, '<p style=\"text-align:justify\"><span style=\"font-size:14px\">VERIFY.GOV.KH is the Cambodian government&rsquo;s platform for document verification.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">It enables stakeholders such as citizens, businesses, and government institutions to view, check, and verify (confirm the authenticity) any document issued by a government agency, public, or private educational establishment with a standard QR code.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">It serves as a single source of truth regarding authenticity of documents issued by the government.</span></p>', 'https://youtu.be/SwXGl7ykJvE', 'Read the full case study', 'https://drive.google.com/file/d/1SWZOfZgGng5HPAZBS1qtzS6tQ_hm2-uc/view', '_blank', 'left', '2025-07-26 04:59:05', '2025-08-06 10:22:51', 'Verify Document Verification • Verifiable Credentials', NULL, NULL, NULL),
(4, 6, '<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Poppins,sans-serif\">Over the past two decades, India has built robust foundational systems, empowering citizens with services ranging from welfare delivery to financial inclusion, in addition to fraud elimination, healthcare benefits, and educational opportunities across sectors.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Poppins,sans-serif\">India&rsquo;s DPI as seen through the lens of common building blocks&nbsp; includes:</span></span></p>\r\n\r\n<ol>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Poppins,sans-serif\"><strong>Verifiable identities</strong>: Aadhaar digital identity</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Poppins,sans-serif\"><strong>Verifiable credentials</strong>: DigiLocker</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Poppins,sans-serif\"><strong>Open network for eCommerce</strong>: Open Network for Digital Commerce (ONDC)</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Poppins,sans-serif\"><strong>Consented data sharing</strong>: Account Aggregator (AA) system</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Poppins,sans-serif\"><strong>Electronic signatures</strong>: eSign</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Poppins,sans-serif\"><strong>Interoperable and instant payments</strong>: UPI for P2P and P2M and Bharat Connect for G2P</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Poppins,sans-serif\"><strong>Open APIs for government services</strong>: Open APIs published by India&#39;s Goods and Services Tax Network (GSTN)</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Poppins,sans-serif\"><strong>Registries</strong>: Farmer registries, health facility registries, teacher registries, or vehicle registries are examples of machine-readable registries</span></span></p>\r\n	</li>\r\n</ol>', 'https://youtu.be/SwXGl7ykJvE', 'Read the full case study', 'https://drive.google.com/file/d/1JVvdTVxDvu1tdLvUxTCB45wbPvawYCfI/view', '_self', 'left', '2025-08-05 11:22:27', '2025-08-06 10:19:53', 'India Stack', 'India Stack', 'India Stack', 'India Stack'),
(5, 7, '<p><span style=\"font-family:Poppins,sans-serif\"><span style=\"font-size:14px\">X-Road provides a robust and secure solution to exchange data and establish a collaborative ecosystem between public and private sectors.</span></span></p>\r\n\r\n<p><span style=\"font-family:Poppins,sans-serif\"><span style=\"font-size:14px\">It originated in Estonia in the late 1990s to address the challenge of isolated government databases and inefficient information sharing.</span></span></p>\r\n\r\n<p><span style=\"font-family:Poppins,sans-serif\"><span style=\"font-size:14px\">Estonia, Finland, and Iceland have jointly funded the Nordic Institute for Interoperability Solutions (NIIS) for the development and strategic management of X-Road. Additionally, Ukraine, the Faroe Islands, and the &Aring;land Islands are partners of NIIS.</span></span></p>', 'https://youtu.be/dkqAceyT7WA', 'Read the full case study', 'https://drive.google.com/file/d/1WfACIjbDsxBWlqPfV9Of5zhVWyEfh_U9/view', '_self', 'left', '2025-08-05 12:48:45', '2025-08-06 10:23:45', 'X-Road • Data Exchange', NULL, NULL, NULL),
(6, 9, '<p>Kyrgyzstan, a nation of about seven million people, embarked on an ambitious digital transformation journey in 2014, driven by the Ministry of Digital Development.</p>\r\n\r\n<p>It combined digital identity and Tunduk interoperability with mobile-first provision of public services.</p>', 'https://youtu.be/MkgSd7QdGok', 'Read full case study', 'https://drive.google.com/file/d/1KT7RPngtG9hkJOvKkErz86g-OPPj0cWm/view', '_self', 'left', '2025-08-06 00:39:47', '2025-08-06 00:39:47', 'Kyrgyzstan', NULL, NULL, NULL),
(7, 10, '<p>Lao PDR has taken the initiative to pilot a &#39;Digital Farmer Registry&#39;.</p>\r\n\r\n<p>The pilot is the first step to transition from basic data aggregation to a more sophisticated, dynamic registry with multiple applications.</p>\r\n\r\n<p>The Lao Digital Farmer Registry was initially tested under the Partnerships for Irrigation and Commercialisation of Smallholder Agricultural (PICSA).</p>\r\n\r\n<p>It will now be scaled up into a larger pilot, integrating another three projects of the Department of Irrigation of the Ministry of Agriculture and Forestry.</p>\r\n\r\n<p>The Lao Digital Farmer Registry is based on OpenSPP, a Digital Public Good (DPG), and is co-built with Newlogic.</p>', 'https://youtu.be/avW8Beaaoxk', 'Read full case study', 'https://drive.google.com/file/d/1KT7RPngtG9hkJOvKkErz86g-OPPj0cWm/view', '_self', 'left', '2025-08-06 00:52:46', '2025-08-06 00:52:46', 'Digital Farmer Registry', NULL, NULL, NULL),
(9, 12, '<p>Launched in 2012, NIBSS Instant Payment (NIP) is Nigeria&#39;s leading digital payment infrastructure. It is owned and operated by the Nigeria Inter-Bank Settlement System (NIBSS), which was established in 1993 and is owned by the Central Bank of Nigeria (CBN) and licensed banks in Nigeria.</p>\r\n\r\n<p>The NIP platform operates 24/7 and serves as an account-based, online real-time Electronic Funds Transfer (EFT) system.</p>\r\n\r\n<p>NIP has evolved into a comprehensive payment platform.</p>\r\n\r\n<p>In 2023, 13% of Nigeria&#39;s 224M projected population used NIP for transactions, up by nearly 77% from 2020.</p>', 'https://youtu.be/95pz6seLtYc', 'Read full case study', 'https://drive.google.com/file/d/1Bg0WVYHe0enr7l7Pe0Jz6NtZ2C3g7pUX/view', '_self', 'left', '2025-08-06 01:00:34', '2025-08-06 01:00:34', 'NIP • Instant Payments', NULL, NULL, NULL),
(10, 13, '<p>Singapore&rsquo;s journey towards digital transformation is driven by the Infocomm Media Development Authority (IMDA). In Singapore, Digital Public Infrastructure (DPI) is referred to as Digital Utilities (DUs). Singapore&rsquo;s Digital Utility (SGDU) stack has four major pillars to serve citizens and businesses:</p>\r\n\r\n<ol>\r\n	<li>\r\n	<p><strong>Digital ID</strong>: Identification for people and enterprises to participate in digital transactions.&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>E-payments and e-invoicing</strong>: To pay and get paid digitally.&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Document attestation</strong>: To check the authenticity of digital documents.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Data exchange</strong>: Exchange of digital information in a trusted and secure manner.</p>\r\n	</li>\r\n</ol>', 'https://youtu.be/7zJHrNlMnyM', 'Read full case study', 'https://drive.google.com/file/d/1pIGwRWGVTn9_Rbb5DrJCKyoSV-UUbSKq/view', '_self', 'left', '2025-08-06 01:13:52', '2025-08-06 01:13:52', 'Digital Utility Stack', NULL, NULL, NULL),
(11, 14, '<p>South Korea has established a national data platform, which includes the Open Government Data Platform, the Big Data Platform, and the AI Hub.</p>\r\n\r\n<p>These are designed to provide a repository for public and private data, ensuring that data can be accessed and used efficiently across various sectors.</p>\r\n\r\n<p>South Korea has established a robust data ecosystem, which includes advanced networks, data governance frameworks, and the three data platforms.</p>', 'https://youtu.be/IJK-4b2SpKw', 'Read full case study', 'https://drive.google.com/file/d/1deM-5I55-1K-CSoE3ErobwF6MBzlX26W/view', '_self', 'left', '2025-08-06 01:15:56', '2025-08-06 01:15:56', 'Government Data Ecosystem', NULL, NULL, NULL),
(12, 15, '<p>Sri Lanka&rsquo;s National Digital Economy Strategy 2030 aims to build a citizen-centric and &ldquo;connected digital government&rdquo;.</p>\r\n\r\n<p>A &ldquo;foundational and secure digital identity&rdquo; and &ldquo;comprehensive data governance framework&rdquo; are two of the six critical enablers of this vision.</p>\r\n\r\n<p>Sri Lanka&rsquo;s Ministry of Technology (MoT) is focused on building a knowledge-based society through digitally-empowered citizens and building foundational elements for DPI.</p>\r\n\r\n<p>These include the National Data Exchange (NDX) and Sri Lanka Unique Digital Identity (SL-UDI), among others.</p>', 'https://youtu.be/3PNHZ5B5TJI', 'Read full case study', 'https://drive.google.com/file/d/1Ljodhr64vFqTYvAY3Czm_wMWsE2bzwrd/view', '_self', 'left', '2025-08-06 01:20:12', '2025-08-06 01:20:12', 'National Data eXchange', NULL, NULL, NULL),
(13, 16, '<p>The Tanzania technology stack is the foundation for implementation of the 10-year Tanzania Digital Economy Strategy Framework that was launched by the President of Tanzania, Her Excellency Dr. Samia Suluhu Hassan, on July 29, 2024.</p>\r\n\r\n<p>Tanzania&rsquo;s technology stack comprises&nbsp; platforms such as <strong>Jamii Namba</strong> (digital ID), <strong>Jamii Malipo</strong> (digital payments) and <strong>Jamii Data Shirikishi</strong> (Jamii X-Change).</p>\r\n\r\n<p>In addition, these elements are supported by four sublayers &mdash; consent, paperless, cashless and presenceless sublayer.</p>', 'https://youtu.be/32yDZoNXw34', 'Read full case study', 'https://drive.google.com/file/d/1Ljodhr64vFqTYvAY3Czm_wMWsE2bzwrd/view', '_self', 'left', '2025-08-06 01:23:15', '2025-08-06 01:23:15', 'Jamii Platforms', NULL, NULL, NULL),
(14, 17, '<p>PromptPay is a real-time electronic payment system initiated in Thailand in December 2016. It is operated by the National Interbank Transaction Management and Exchange (ITMX) and supervised by the Bank of Thailand (BOT).</p>\r\n\r\n<p>PromptPay was introduced to address pain points in traditional payments: high transaction costs, operational inefficiencies, and limited financial inclusion.</p>\r\n\r\n<p>Upon inception, PromptPay was offered as a low-fee service, but since 2018, payment providers have waived fees for most of its use cases.</p>\r\n\r\n<p>As of June 2024, PromptPay has over 79.5 million registrations, handling 66 million transfers daily.</p>', 'https://youtu.be/jsKDpaPl7ww', 'Read full case study', 'https://drive.google.com/file/d/1Ljodhr64vFqTYvAY3Czm_wMWsE2bzwrd/view', '_self', 'left', '2025-08-06 01:25:19', '2025-08-06 01:25:19', 'PromptPay Instant Payment', NULL, NULL, NULL);

